// react 야 Subject라는 이름의 태그를 나는 만들거야.
// 이 태그의 내용은 아래와 같아.
// 사용자 정의 태그를 만들 수 있다.
import React, { Component, useState } from "react";

//property => props
class Subject extends Component {
  render() {
    return (
      <header>
        <h1>{props.title}</h1>
        {props.sub}
      </header>
    );
  }
}

function Toc() {
  return (
    <nav>
      <ol>
        <li>
          <a href="1.html">HTML</a>
        </li>
        <li>
          <a href="2.html">CSS</a>
        </li>
        <li>
          <a href="3.html">Javascript</a>
        </li>
      </ol>
    </nav>
  );
}

function Content(props) {
  return (
    <article>
      <h2>{props.title}</h2>
      {props.sub}
    </article>
  );
}
function App(props) {
  return (
    <div className="App">
      <Subject title="WEB" sub="World wide web"></Subject>

      <Toc></Toc>

      <Content title="Welcom" sub="Hello web"></Content>
    </div>
  );
}

export default App;
